const chat = document.getElementById('chat');const orderSummary = document.getElementById('order-summary');const placeOrderButton = document.getElementById('place-order');let order = [];function displayMessage(message, sender) {const messageDiv = document.createElement('div');messageDiv.textContent = `${sender}: ${message}`;chat.appendChild(messageDiv);}function updateOrderSummary() {orderSummary.innerHTML = '<h2>Order Summary</h2>';order.forEach(item => {const itemDiv = document.createElement('div');itemDiv.textContent = item;orderSummary.appendChild(itemDiv);});}displayMessage('Welcome! What pizza would you like to order?', 'Agent');placeOrderButton.addEventListener('click', () => {displayMessage('Order placed!', 'Agent');});